import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CLUE_LIST = ["shortest path", "unweighted", "weighted", "sorted", "contiguous", "subarray"];
const TECH_LIST = ["BFS", "Dijkstra", "Sliding Window", "Two Pointers", "DP"];

function App() {
    const [problems, setProblems] = useState([]);
    const [current, setCurrent] = useState(0);
    const [selectedClues, setSelectedClues] = useState([]);
    const [selectedTech, setSelectedTech] = useState("");
    const [result, setResult] = useState(null);

    useEffect(() => {
        axios.get('http://127.0.0.1:8000/problems').then(res => setProblems(res.data));
    }, []);

    const handleVerify = async () => {
        const res = await axios.post('http://127.0.0.1:8000/validate', {
            id: problems[current].id,
            tech: selectedTech,
            clues: selectedClues
        });
        setResult(res.data);
    };

    if (!problems.length) return <div>Loading Problem Set...</div>;

    return (
        <div style={{ padding: '2rem', maxWidth: '900px', margin: 'auto', fontFamily: 'monospace' }}>
            <h1>SYSTEM_TRAINER_V1</h1>
            <div style={{ border: '1px solid #333', padding: '1rem', marginBottom: '1rem' }}>
                <h3>{problems[current].title}</h3>
                <p>{problems[current].description}</p>
            </div>

            <h4>1. Identify Description Clues:</h4>
            {CLUE_LIST.map(c => (
                <button key={c} onClick={() => setSelectedClues([...selectedClues, c])}
                    style={{ background: selectedClues.includes(c) ? '#00ff00' : '#eee', margin: '5px' }}>
                    {c}
                </button>
            ))}

            <h4>2. Classify Technique:</h4>
            <select onChange={(e) => setSelectedTech(e.target.value)} style={{ width: '100%', padding: '10px' }}>
                <option>--- Select Mechanic ---</option>
                {TECH_LIST.map(t => <option key={t} value={t}>{t}</option>)}
            </select>

            <button onClick={handleVerify} style={{ marginTop: '20px', padding: '10px 20px', cursor: 'pointer' }}>
                VALIDATE REASONING
            </button>

            {result && (
                <div style={{ marginTop: '20px', padding: '10px', background: result.success ? '#d4edda' : '#f8d7da' }}>
                    {result.success ? "ANALYSIS_LOCKED: Correct Invariants identified." : "ANALYSIS_FAILED: Review description signals."}
                    <pre>{JSON.stringify(result.feedback, null, 2)}</pre>
                </div>
            )}
        </div>
    );
}
export default App;
