import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CLUE_LIST = ["Two Pointers", "Monotonic Stack", "Sliding Window", "Binary Search", "DFS", "BFS", "Hash Table", "Dynamic Programming"];

function App() {
    const [problems, setProblems] = useState([]);
    const [patterns, setPatterns] = useState({});
    const [currentIdx, setCurrentIdx] = useState(0);
    const [view, setView] = useState('dashboard'); // 'dashboard' or 'trainer'
    const [selectedClues, setSelectedClues] = useState([]);
    const [result, setResult] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [probRes, pattRes] = await Promise.all([
                    axios.get('http://127.0.0.1:8000/problems'),
                    axios.get('http://127.0.0.1:8000/patterns')
                ]);
                setProblems(probRes.data);
                setPatterns(pattRes.data);
                setLoading(false);
            } catch (err) {
                console.error("Connection Error:", err);
                setLoading(false);
            }
        };
        fetchData();
    }, []);

    const toggleClue = (clue) => {
        setSelectedClues(prev =>
            prev.includes(clue) ? prev.filter(c => c !== clue) : [...prev, clue]
        );
    };

    const handleVerify = async () => {
        try {
            const res = await axios.post('http://127.0.0.1:8000/validate', {
                problem_id: problems[currentIdx].id,
                user_clues: selectedClues
            });
            setResult(res.data);
        } catch (e) {
            setResult({ success: false, feedback: "Validation server unreachable." });
        }
    };

    const selectFromDashboard = (id) => {
        const index = problems.findIndex(p => p.id === id);
        if (index !== -1) {
            setCurrentIdx(index);
            setView('trainer');
            setResult(null);
            setSelectedClues([]);
        }
    };

    if (loading) return <div style={styles.loader}>INITIALIZING_SYSTEM_TRAINER...</div>;

    // SAFETY CHECK: If we are in trainer mode but have no problems, show an error or redirect
    if (view === 'trainer' && (!problems || problems.length === 0)) {
        return (
            <div style={styles.loader}>
                <h2>NO_DATA_AVAILABLE</h2>
                <button onClick={() => setView('dashboard')}>Return to Dashboard</button>
            </div>
        );
    }
    return (
        <div style={styles.container}>
            {/* Unified Sidebar */}
            <div style={styles.sidebar}>
                <button onClick={() => setView('dashboard')} style={styles.navBtn}>ðŸ“Š DASHBOARD</button>
                <button onClick={() => setView('trainer')} style={styles.navBtn}>ðŸŽ¯ TRAINER</button>
                <hr style={{ borderColor: '#333', margin: '1rem 0' }} />
                <h3 style={{ color: '#888', fontSize: '0.7rem' }}>PROB_MANIFEST</h3>
                {problems.map((p, idx) => (
                    <div
                        key={p.id}
                        onClick={() => { setCurrentIdx(idx); setView('trainer'); setResult(null); setSelectedClues([]); }}
                        style={{
                            ...styles.sideItem,
                            background: (currentIdx === idx && view === 'trainer') ? '#333' : 'transparent',
                            color: (currentIdx === idx && view === 'trainer') ? '#00ff00' : '#ccc'
                        }}
                    >
                        {idx + 1}. {p.title.substring(0, 20)}
                    </div>
                ))}
            </div>

            {/* Content Area */}
            <div style={styles.main}>
                {view === 'dashboard' ? (
                    <div style={styles.dashboardContainer}>
                        <h1>PATTERN_DASHBOARD</h1>
                        <div style={styles.dashboardGrid}>
                            {Object.entries(patterns).map(([pillar, items]) => (
                                <div key={pillar} style={styles.pillarCard}>
                                    <h3 style={styles.pillarTitle}>{pillar.replace('_', ' ')}</h3>
                                    <div style={styles.countBadge}>{items.length} Elements</div>
                                    <div style={styles.miniList}>
                                        {items.map(item => (
                                            <div key={item.id} onClick={() => selectFromDashboard(item.id)} style={styles.miniLink}>
                                                â€¢ {item.title}
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                ) : (
                    <div style={styles.trainerContainer}>
                        <div style={styles.header}>
                            <h1>{problems[currentIdx].title}</h1>
                            <div style={styles.badgeRow}>
                                <span style={styles.badge}>TIME: {problems[currentIdx].complexity_time || 'O(N)'}</span>
                                <span style={styles.badge}>SPACE: {problems[currentIdx].complexity_space || 'O(1)'}</span>
                            </div>
                        </div>

                        <div style={styles.card}>
                            <h3>PROBLEM_DESCRIPTION</h3>
                            <div style={styles.description}>{problems[currentIdx].description}</div>
                        </div>

                        {/* Implementation Section (Multi-Language Learning) */}
                        {problems[currentIdx].implementations?.length > 0 && (
                            <div style={{ marginTop: '2rem' }}>
                                <h3>POLYGLOT_REFERENCE</h3>
                                <ImplementationViewer impls={problems[currentIdx].implementations} />
                            </div>
                        )}

                        <div style={styles.logicSection}>
                            <h3>IDENTIFY_MECHANICS</h3>
                            <div style={styles.clueGrid}>
                                {CLUE_LIST.map(clue => (
                                    <button
                                        key={clue}
                                        onClick={() => toggleClue(clue)}
                                        style={{
                                            ...styles.clueBtn,
                                            backgroundColor: selectedClues.includes(clue) ? '#00ff00' : '#1a1a1a',
                                            color: selectedClues.includes(clue) ? '#000' : '#fff'
                                        }}
                                    >
                                        {clue}
                                    </button>
                                ))}
                            </div>
                            <button onClick={handleVerify} style={styles.verifyBtn}>VALIDATE_REASONING</button>
                        </div>

                        {result && (
                            <div style={{ ...styles.resultCard, borderColor: result.success ? '#00ff00' : '#ff4444' }}>
                                <h4>{result.success ? ">>> VALIDATION_PASSED" : ">>> LOGIC_INCOMPLETE"}</h4>
                                <pre style={{ whiteSpace: 'pre-wrap' }}>{result.feedback}</pre>
                            </div>
                        )}
                    </div>
                )}
            </div>
        </div>
    );
}

function ImplementationViewer({ impls }) {
    const [lang, setLang] = useState(impls[0]?.language || 'python');
    const active = impls.find(i => i.language === lang);

    return (
        <div style={styles.implContainer}>
            <div style={styles.tabs}>
                {impls.map(i => (
                    <button key={i.language} onClick={() => setLang(i.language)}
                        style={{ ...styles.tab, borderBottom: lang === i.language ? '2px solid #00ff00' : 'none', color: lang === i.language ? '#00ff00' : '#888' }}>
                        {i.language.toUpperCase()}
                    </button>
                ))}
            </div>
            <pre style={styles.codeBlock}>{active?.code || "// Implementation missing"}</pre>
        </div>
    );
}

